mapper.insert:
insert into
tb_course_type_relation(
course_id,
course_type_id
) values(
#{courseId},
#{typeId}
)

mapper.select:
select count(*) from tb_course c join tb_course_type_relation ctr on c.id=ctr.course_id
and c.name like '%${name}%'
and ctr.course_type_id=#{typeId}

select c.id, c.name, c.user_id as userId, c.description, c.created_time as createdTime from tb_course c join tb_course_type_relation ctr on c.id=ctr.course_id
and c.name like '%${name}%'
and ctr.course_type_id=#{typeId}
